function time()
{
    var today = new Date();

    var hour = today.getHours();
    if (hour<10) hour = "0" + hour;

    var minute = today.getMinutes();
    if (minute<10) minute = "0" + minute;

    var secound = today.getSeconds();
    if (secound<10) secound = "0" + secound;
    
    var year = today.getFullYear();
    var month = today.getMonth() + 1;
    if (month<10) month = "0" + month;
    var day = today.getDate();
    if (day<10) day = "0" + day;

    if (document.getElementById("fulldate"))
    {
        document.getElementById("fulldate").innerHTML = day + "/" + month + "/" + year + "|" + hour + ":" + minute + ":" + secound;
    }
    if (document.getElementById("day"))
    {
        document.getElementById("day").innerHTML = day;
    }
    if (document.getElementById("month"))
    {
        document.getElementById("month").innerHTML = month;
    }
    if (document.getElementById("year"))
    {
        document.getElementById("year").innerHTML = year;
    }
    if (document.getElementById("hour"))
    {
        document.getElementById("hour").innerHTML = hour
    }
    if (document.getElementById("minute"))
    {
        document.getElementById("minute").innerHTML = minute;
    }
    if (document.getElementById("secound"))
    {
        document.getElementById("secound").innerHTML = secound;
    }

    if (document.getElementById("dayofweek"))
    {
        var weekday = new Array(7);
        weekday[0] =  "Sunday";
        weekday[1] = "Monday";
        weekday[2] = "Tuesday";
        weekday[3] = "Wednesday";
        weekday[4] = "Thursday";
        weekday[5] = "Friday";
        weekday[6] = "Saturday";

        document.getElementById("dayofweek").innerHTML = weekday[today.getDay()];
    }
    setTimeout("time()", 1000)
}
function countup(from, to, ms, id)
{
    
    if (from == to)
    {
        clearTimeout(timeout);
    }
    else
    {
        from++;
        document.getElementById(id).innerHTML = from;
    }
    var timeout = setTimeout(countup, ms, from, to, ms, id);
}
function countdown(from2, to2, ms2, id2, funct)
{
    
    if (from2 == to2)
    {
        clearTimeout(timeout2);
        window[funct]();
        return;
    }
    else
    {
        from2--;
        document.getElementById(id2).innerHTML = from2;
    }
    var timeout2 = setTimeout(countdown, ms2, from2, to2, ms2, id2, funct);
}